import React, { useState } from "react"

function App() {

    const [numbers, setNumbers] = useState({
        total: 456,
        calcs: "54+236-3*.3",
        calcsArray: [3, "+", 2, "-", `(`, 1, `*`, 2, `)`, "+", .3]  // should return 3.3, not 8.3
    })

    // const [inputs, setInputs] = useState({
    //     calcs: "54+236-3*.3"
    // })

    function doMath(array) {
        // for (i = 0; i < array.length; i++) {
        //don't need this for loop?
        // }
        let math = array
    }

    function parenthesesSearch(array) {
        let firstEnd = array.indexOf(")")
        let closestStart = array.lastIndexOf("(", firstEnd)
        let parenthLocations = [closestStart, firstEnd]
        return console.log(parenthLocations)
    }

    parenthesesSearch(numbers.calcsArray)

    return (
        <div id="box">
            <div id="calculator">

                <div id="screen">
                    <div id="calcs">{numbers.calcs}</div>
                    <div id="answer-row">{numbers.total}</div>
                </div>

                <div id="erase"></div>
                <div id="back"></div>
                <div id="divide"></div>
                <div id="7"></div>
                <div id="8"></div>
                <div id="9"></div>
                <div id="multiply"></div>
                <div id="4"></div>
                <div id="5"></div>
                <div id="6"></div>
                <div id="subtract"></div>
                <div id="1"></div>
                <div id="2"></div>
                <div id="3"></div>
                <div id="add"></div>
                <div id="0"></div>
                <div id="switch"></div>
                <div id="dot"></div>
                <div id="equals"></div>
            </div>

        </div>
    )
}

export default App
